import React, { useState } from 'react'
import "./feedback.css"
import axios from 'axios'
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Rating from '@mui/material/Rating';
import Button from '@mui/material/Button';
import Divider from '@mui/material/Divider';
import Grid from '@mui/material/Grid';
import Masonry from 'react-masonry-css'
import { format } from 'date-fns'
import Stack from '@mui/material/Stack';
import { positions } from '@mui/system';
import PersonIcon from '@mui/icons-material/Person';
import SendIcon from '@mui/icons-material/Send';
import { Avatar, CardHeader, Container, Drawer, Icon, IconButton, makeStyles, Paper, Table, TableCell, TableContainer, TableHead, TableRow, TextField } from '@mui/material';
import { shadows } from '@mui/system';
import Navbar from '../../components/navigation'
// import Footer from '../../components/footer'

const labels = {
  0.5: 'Useless',
  1: 'Useless+',
  1.5: 'Poor',
  2: 'Poor+',
  2.5: 'Ok',
  3: 'Ok+',
  3.5: 'Good',
  4: 'Good+',
  4.5: 'Excellent',
  5: 'Excellent+',
};

const bull = (
  <Box
    component="span"
    sx={{ display: 'inline-block', mx: '2px', transform: 'scale(0.8)' }}
  >
    â€¢
  </Box>
);

function Feedback() {
  const [value, setValue] = React.useState(2);
  const [hover, setHover] = React.useState(-1);

  const [title, setTitle] = useState('')
  const [feedback, setFeedback] = useState('')

  const [titleError, setTitleError] = useState('false')
  const [feedbackError, setFeedbackError] = useState('false')

  const handleSubmit = (e) => {
    e.preventDefualt()
    setTitleError(false)
    setFeedbackError(false)

    if (title === '') {
      setTitleError(true)
    }

    if (feedback === '') {
      setFeedbackError(true)
    }

    if (title && feedback) {
      console.log(title, feedback)
    }
  }

  const breakpoints = {
    default: 3,
    1100: 2,
    700: 1
  }

  const [status, setStatus] = useState({
    submitted: false,
    submitting: false,
    info: { error: false, msg: null }
  })
  const [inputs, setInputs] = useState({
    //email: '',
    //message: ''
  })

  const handleServerResponse = (ok, msg) => {
    if (ok) {
      setStatus({
        submitted: true,
        submitting: false,
        info: { error: false, msg: msg }
      })
      setInputs({
        email: '',
        message: ''
      })
    } else {
      setStatus({
        info: { error: true, msg: msg }
      })
    }
  }
  const handleOnChange = e => {
    e.persist()
    setInputs(prev => ({
      ...prev,
      [e.target.id]: e.target.value
    }))
    setStatus({
      submitted: false,
      submitting: false,
      info: { error: false, msg: null }
    })
  }

  const handleOnSubmit = e => {
    e.preventDefault()
    setStatus(prevStatus => ({ ...prevStatus, submitting: true }))
    axios({
      method: 'POST',
      url: 'https://formspree.io/f/xqkwrnpg',
      data: inputs
    })
      .then(response => {
        handleServerResponse(
          true,
          'Thank you, your message has been submitted.'
        )
      })
      .catch(error => {
        handleServerResponse(false, error.response.data.error)
      })
  }

