    <Container style={{ display: 'flex', marginRight: 0, marginLeft: 0 }} style={{ maxWidth: '1400px'}}>

      <TableContainer component={Paper} sx={{ mb: '0'}} > 
        <Table>
          <TableHead>
            <TableRow>
              <TableCell style={{ width: '20%' }} sx={{ borderRight: 1, textAlign: 'center', verticalAlign: 'baseline' }}>  
                <div>
                  <Typography variant="h5" letterSpacing={8} sx={{ mx: 5, my: 1, textAlign: 'center', fontWeight: 600 }}>
                    Feedback Form
                  </Typography>
                  <Divider />
                  <form noValidate autoComplete="off" onSubmit={handleOnSubmit} sx={{ mx: 5, mt: 2 }}>
                    <TextField
                      id="name"
                      //onChange={(e) => setTitle(e.target.value)}
                      onChange={handleOnChange}
                      label="Name:"
                      sx={{ mt: 5, width: "90%" }}
                      required
                      name="_name"
                      value={inputs.name}
                    //error={titleError}
                    />
                    <br /><br />
                    <TextField
                      id="querymsg"
                      //onChange={(e) => setFeedback(e.target.value)}
                      onChange={handleOnChange}
                      label="Feedback:"
                      name="message"
                      multiline
                      rows={4}
                      sx={{ width: "90%" }}
                      required
                      value={inputs.feedbackmsg}
                    //error={feedbackError}
                    />
                    <TextField
                      id="feedbackmsg"
                      //onChange={(e) => setFeedback(e.target.value)}
                      onChange={handleOnChange}
                      label="Query:"
                      name="message"
                      multiline
                      rows={4}
                      sx={{ width: "90%" }}
                      required
                      value={inputs.feedbackmsg}
                    //error={feedbackError}
                    />
                    <br /><br />
                    <Rating
                      name="hover-feedback"
                      value={value}
                      defaultValue={2}
                      //style={{ textAlign: "left"}}
                      sx={{ width: '100%' }}
                      sx={{ justifyContent: 'flex-start' }}
                      size='large'
                      left
                      precision={0.5}
                      onChange={(event, newValue) => {
                        setValue(newValue);
                      }}
                      onChangeActive={(event, newHover) => {
                        setHover(newHover);
                      }}
                    />
                    {value !== null && (
                      <Box sx={{ ml: 2 }}>{labels[hover !== -1 ? hover : value]}</Box>
                    )}
                    <br />
                    <Button
                      type="submit"
                      variant="contained"
                      disabled={status.submitting}
                      sx={{ width: "90%" }}
                      endIcon={<SendIcon />}
                    >
                      {!status.submitting
                        ? !status.submitted
                          ? 'Submit'
                          : 'Submitted'
                        : 'Submitting...'}
                    </Button>
                  </form>
                  {status.info.error && (
                    <div className="error">Error: {status.info.msg}</div>
                  )}
                  {!status.info.error && status.info.msg && <p>{status.info.msg}</p>}
                </div>
